from selenium import webdriverfrom selenium.webdriver.common.keys import Keysimport unittestclass NewVisitorTest(unittest.TestCase):    def setUp(self):        self.browser = webdriver.Firefox()        self.browser.implicitly_wait(3)    def tearDown(self):        self.browser.quit()    #helper function    def check_for_row_in_list_table(self, row_text):        table = self.browser.find_element_by_id('id_list_table')        rows = table.find_elements_by_tag_name('tr')        self.assertIn(row_text, [row.text for row in rows])    def test_can_start_a_list_and_retrieve_it_later(self):        self.browser.get('http://localhost:8000')        self.assertIn('To-Do', self.browser.title)        header_text = self.browser.find_element_by_tag_name('h1').text        self.assertIn('To-Do', header_text)        inputbox = self.browser.find_element_by_id('id_new_item')        self.assertEqual(inputbox.get_attribute('placeholder'), 'Enter a to-do item')        # User adds "Buy pirate hat" as a new item        inputbox.send_keys('Buy pirate hat')        inputbox.send_keys(Keys.ENTER)        #page updates and shows the new item        self.check_for_row_in_list_table('1: Buy pirate hat')        # User adds "Find a goold old sword"        inputbox = self.browser.find_element_by_id('id_new_item')        inputbox.send_keys('Find a good old sword')        inputbox.send_keys(Keys.ENTER)        # The page updates again, and now shows both items on her list        self.check_for_row_in_list_table('1: Buy pirate hat')        self.check_for_row_in_list_table('2: Find a good old sword')        self.fail('Finish the test!')        # The page updates again, and now shows both items on her listif __name__ == '__main__':    unittest.main(warnings='ignore')